import {useEffect, useState} from 'react';
import axios from "axios";

function Chat() {
    const [chats,setChats]= useState(['']);

    useEffect(() => {
       
    })
    const setChat = (e) =>{
        setChats(e.target.value);
    };
    const sendChat = async () => {
      try{
        await axios.post('http://127.0.0.1:5000', {chat: chats});
        setChats(['']);
        console.log('Chat sent:', chats);

      } catch (error) {
        console.error('Error sending chat:', error);
      };
    }


    return (
            <div className="terminal">
              <div className="terminal-header">
                <span className="led led-red" />
                <span className="led led-yellow" />
                <span className="led led-green" />
                <span className="title">/chat/terminal</span>
              </div>
              <div className="terminal-body" >
                  <div className="line">
                  </div>
              </div>
              <div className="terminal-input">
                <span className="prompt">{" > "}</span>
                <textarea
                  rows={1}
                  placeholder="type here and press Enterâ€¦ (Shift+Enter for newline)"
                  onChange={setChat}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      sendChat();
                    }
                  }}
                  value={chats}
                />
              </div>
            </div>
    );
}
export default Chat;
